SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les pnii apparaissent vers 22 sa. le surfactant est secrete a 36 sa. naissance prematuree &amp;lt; 36 sa → surfactant insuffisant. en resume, le surfactant: augmente a compliance pulmonaire → diminue le travail inspiratoire, stabilise les alveoles. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
