SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;cette relation existe toujours chez le sujet emphysemateux, bien que la &lt;/span&gt;&lt;span &gt;raw&lt;/span&gt;&lt;span &gt; soit plus elevee pour un meme volume. ceci est du, et nous en avons deja parle, au fait la trame parenchymateuse et detruite dans cette pathologie, par consequent la traction radiale que le parenchyme exerce sur la paroi bronchique diminue.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
