SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le poumon humain contient environ 500 millions d'alveoles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'organisation du tissu pulmonaire en alveoles permet d'assurer une grande surface d'echange : environ 50 a 100 m2, dans un volume limite qui est celle de la cage thoracique.&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces alveoles sont enveloppes dans un dense reseau de capillaires. chaque&lt;/span&gt;&lt;span &gt; alveole a un &lt;/span&gt;&lt;span &gt;diametre &lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;200 a 250 µm&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le nombre de capillaires est estime a 280 x 10&lt;/span&gt;&lt;span &gt;9&lt;/span&gt;&lt;span &gt; ou environ 500 a 1000 par alveole.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il en resulte une surface d'echange important : 50 a 100 m&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; . cette surface d'echange est la zone ou le sang vient effectivement au contact de l'air. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@organisation du systeme respiratoire @";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide3.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme&lt;/span&gt;&lt;span &gt; nous le verrons dans le chapitre sur la diffusion, l'&lt;/span&gt;&lt;span &gt;interface entre le sang et l'air est assuree par l'ensemble de l'epithelium alveolaire - et de l'endothelium capillaire, separes par leurs membranes basales&lt;/span&gt;&lt;span &gt; qui sont fusionnees. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cet&lt;/span&gt;&lt;span &gt; ensemble forme une membrane qui &lt;/span&gt;&lt;span &gt;est extremement mince : 0.2 a 0.5 µm.&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le fait que la membrane d'echange entre l'air et le sang soit ait une tres grande surface et une epaisseur tres fine est importante pour faciliter la diffusion de l'oxygene et du gaz carbonique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
