SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il est important de faire quelques remarques au sujet de l'organisation&lt;/span&gt;&lt;span &gt; structurelle des voies aeriennes: c'est-a-dire celles qui permettent de conduire l'air de la glotte jusqu'au alveoles. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces&lt;/span&gt;&lt;span &gt; voies aeriennes ont une structure qui ressemble a un arbre: l&lt;/span&gt;&lt;span &gt;a trachee se divise en deux bronches principales&lt;/span&gt;&lt;span &gt; ou bronches &lt;/span&gt;&lt;span &gt;souches droite et gauche qui se subdivisent a leur tour en bronches lobaires puis segmentaires.&lt;/span&gt;&lt;span &gt; cette division continue de generation en generation. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce tableau resume les dimensions, le nombre, et la surface de coupe totales de ces bronches, en fonction du nombre de generations. il ne s'agit absolument pas de retenir ces chiffres par cœur, mail ils sont a pour expliquer des points importants pour la fonction pulmonaire; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous voyez qu'au fil des generations, alors que le diametre des bronches diminuent, leurs nombre augmente, si bien que si on ajoute la surface de section de toutes le bronches dans une meme generation, elle augmente rapidement. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;bronchioles terminales&lt;/span&gt;&lt;span &gt; sont les plus petites bronches depourvues d'alveoles. les bronchioles sont des structures dont la paroi est depourvue de cartilage, il s'agit donc de conduits compressibles. nous verrons la signification physiologique de cette propriete plus tard. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le role des bronches qu'on appelle « de conduction » est d'amener les gaz jusqu'aux zones d'echanges gazeux. puisque ces voies aeriennes sont depourvues d'alveoles, elles ne participent pas aux echanges gazeux. ils constituent &lt;/span&gt;&lt;span  fontweight='bold'&gt;l'espace mort anatomique&lt;/span&gt;&lt;span &gt;, dont le volume est d'environ 150 ml chez l'adulte. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il faut retenir en revanche que l'ensemble de l'arbre bronchique comporte environ 23 generations. les 16 premieres generations sont composees de voies de conduction. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide6.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les &lt;/span&gt;&lt;span  fontweight='bold'&gt;bronchioles terminales&lt;/span&gt;&lt;span &gt; se divisent pour former les bronchioles respiratoires (g17 - g19) &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les zones pulmonaires au-dela de la bronchiole terminales composent &lt;/span&gt;&lt;span &gt;l'&lt;/span&gt;&lt;span  fontweight='bold'&gt;ascinus&lt;/span&gt;&lt;span &gt; : l'unite anatomique et fonctionnelle ou a lieu l'echange gazeux &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@organisation du systeme respiratoire @";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide7.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;consequence de l'architecture arborescente des voies aeriennes : la surface de section augmente au fil des generations &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une consequence importante de cette organisation structurelle est que pour&lt;/span&gt;&lt;span &gt; un meme debit de l'air inhale, la vitesse du flux d'air diminue beaucoup au fil des generations, jusqu'au niveau des acini ou flux atteint sa vitesse minimale. le principal mecanise de transport devient alors la diffusion. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
