SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;organisation&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;generale&lt;/span&gt;&lt;span &gt; de la circulation. circulation &lt;/span&gt;&lt;span &gt;systemique&lt;/span&gt;&lt;span &gt; : vg &lt;/span&gt;&lt;span &gt;propulse&lt;/span&gt;&lt;span &gt; le sang &lt;/span&gt;&lt;span &gt;dans&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;l'ao&lt;/span&gt;&lt;span &gt; qui &lt;/span&gt;&lt;span &gt;alimente&lt;/span&gt;&lt;span &gt; tout la &lt;/span&gt;&lt;span &gt;ciculation&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;systemique&lt;/span&gt;&lt;span &gt;, le sang &lt;/span&gt;&lt;span &gt;revient&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;vers&lt;/span&gt;&lt;span &gt; le &lt;/span&gt;&lt;span &gt;coeur&lt;/span&gt;&lt;span &gt; au n de &lt;/span&gt;&lt;span &gt;l'od&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;puis&lt;/span&gt;&lt;span &gt; la circulation &lt;/span&gt;&lt;span &gt;pulmonaire&lt;/span&gt;&lt;span &gt; : le vd &lt;/span&gt;&lt;span &gt;propulse&lt;/span&gt;&lt;span &gt; le sang &lt;/span&gt;&lt;span &gt;dans&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;l'ap&lt;/span&gt;&lt;span &gt; qui &lt;/span&gt;&lt;span &gt;alimente&lt;/span&gt;&lt;span &gt; les &lt;/span&gt;&lt;span &gt;capillaire&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;pulm&lt;/span&gt;&lt;span &gt; et le sang &lt;/span&gt;&lt;span &gt;est&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;draine&lt;/span&gt;&lt;span &gt; par les &lt;/span&gt;&lt;span &gt;veines&lt;/span&gt;&lt;span &gt; (4)  &lt;/span&gt;&lt;span &gt;pulm&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;vers&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;l'og&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en raison de cette organisation en serie, la circulation pulmonaire recoit tout le debit cardiaque, soit 5 a 6 l/in ou 3.5 l/min/m2 de surface corporelle. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;250 a 300 ml de sang / m2 de surface corporelle dans la circulation pulmonaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
