SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les divisions arterio-veineuses suivent celles du reseau bronchique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;arteres proches des voies de conduction aerienne&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;veines separees de l'ensemble artere-bronche (inclues dans travees conjonctives)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;au repos, il faut 4 a 5 sec pour un gr de traverser la circulation pulmonaire; 0.75 sec dans les capillaires pulm. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;particularite structurale des arteres pulmonaire: leur paroi est beaucoup lus mince que les artere systemiques de diametre comparable.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;consequence fonctionnelle: la paroi de ces arteres est plus distensible et compressible. nous verrons que cette structure est tout a fait adaptee aux pressions dans la circulation pulmonaire qui est beaucoup plus basse que celle dans la circulation systemique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
