SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;lorsque l'on gonfle le poumon en inspiration, la pression pleurale diminue, par consequent la p &lt;/span&gt;&lt;span &gt;transmurale&lt;/span&gt;&lt;span &gt; des ces vx extra-alveolaire augmente, ce qui augmente leur rayon. par consequent leur resistance diminue. vous voyez donc que l'augmentation du volume pulmonaire a un effet inverse sur les vx intra-&lt;/span&gt;&lt;span &gt;alveolaores&lt;/span&gt;&lt;span &gt;, &lt;/span&gt;&lt;span &gt;cad&lt;/span&gt;&lt;span &gt; en majorite des capillaires, et les vx extra-alveolaires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
