SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'hypoxie, c'est-a-dire une baisse de la po2 de l'air alveolaire entraine une vasoconstriction des arterioles pulmonaire. dans cette figure vous voyez deux alveoles voises alimentees par 2 arterioles differentes. elles sont bien ventilees et perfusee toutes les deux, ce qui permet les echanges gazeux efficaces. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;si la pao2 diminue dans l'une d'elles//, par exemple a cause d'une hypoventilation alveolaire due a une obstruction de la bronche, cela entraine une constriction de l'arteriole qui alimente cette zone alveolaire //. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par consequent la perfusion sanguine dans ce territoire diminue et la &lt;/span&gt;&lt;span &gt;pefusion&lt;/span&gt;&lt;span &gt; sanguine est redirigee vers les zones ou la ventilation alveolaire par consequent la pao2 est normale. la consequence de ce phenomene est d'ameliorer les echanges gazeux en adaptant en permanence la perfusion sanguine a la ventilation alveolaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@hemodynamique : vasoconstriction hypoxique @";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
