SDpagedisplay[SDpagenum] = "Slide35.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'hypoxie semble agir directement sur la cellule musculaire lisse. pour autant le mecanisme intime de cette vasoconstriction hypoxique n'est pas connue. la vasoconstriction hypoxique persiste sur les vx pulmonaire isoles ou dans un poumon transplante. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

