SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide36.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il s'agit d'une reaction adaptative qui peut avoir un role benefique si l'hypoxie est localisee a une zone pulmonaire, elle permet de devier la perfusion sanguine vers les zones bien ventilees. egalement, chez le fœtus, seulement 15% du debit cardiaque perfuse le poumon, et il a ete demontre que cela est due a la grande resistance des vx pulmonaires a cause de la vasoconstriction hypoxique, car le poumon n'est pas ventilees. une des raisons pour lesquelles la perfusion du poumon augmente a la naissance est la levee de cette vasoconstriction hypoxique par l'aeration et l'oxygenation des alveoles. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
