SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en revanche l'effet de la vasoconstriction hypoxique peut etre deletere si l'hypoxie est generalisee, par exemple lors d'un sejour en altitude ou nous avons vu que la pression partielle en o2 de l'air ambiant diminue ainsi que la pression barometrique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
