SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;si toute la va etait distribuee au poumon d, et tout le debit au poumon g, le rapport global serait toujours = 1, mais les echanges gazeux ne pourraient pas avoir lieu.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le rapport va/q autrement dit la correspondance&lt;/span&gt;&lt;span &gt; entre le debit sanguin et la ventilation alveolaire dans chaque petite region du poumon par exemple un lobule ou meme un acinus pulmonaire, &lt;/span&gt;&lt;span &gt;determine les echanges gazeux pulmonaire globaux autrement dit la po2 et la pco2 que l'on va retrouver dans le sang arteriel&lt;/span&gt;&lt;span &gt; systemique. &lt;/span&gt;&lt;span &gt;  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
