SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la concentration d'o&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; et de co&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; dans chaque alveole est determine a tout moment par: &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'apport d'o&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; frais et l'evacuation de co&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; par la &lt;/span&gt;&lt;span &gt;v ̇&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;alveolaire&lt;/span&gt;&lt;span &gt; d'une part, et d'autre part par &lt;/span&gt;&lt;span &gt;l'apport &lt;/span&gt;&lt;span &gt;de co&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; et le prelevement d'o&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; par la&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;q ̇&lt;/span&gt;&lt;span &gt; sanguine des alveoles. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les &lt;/span&gt;&lt;span &gt;modifications du rapport &lt;/span&gt;&lt;span &gt;v ̇∕q ̇ &lt;/span&gt;&lt;span &gt; ont un impact direct sur les echanges gazeux au niveau alveolaire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
