SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme il s'agit d'un equilibre dynamique, la perfusion sanguine continue d'apporter du co2, et de&lt;/span&gt;&lt;span &gt; prelever l'o2. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lorsque la v&lt;/span&gt;&lt;span &gt;a&lt;/span&gt;&lt;span &gt; est completement abolie: &lt;/span&gt;&lt;span &gt;v ̇∕q ̇ &lt;/span&gt;&lt;span &gt; = 0 &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;p&lt;/span&gt;&lt;span &gt;a&lt;/span&gt;&lt;span &gt;o&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt; et p&lt;/span&gt;&lt;span &gt;a&lt;/span&gt;&lt;span &gt;co&lt;/span&gt;&lt;span &gt;2 &lt;/span&gt;&lt;span &gt;de l'air alveolaire s'equilibrent avec le sang veineux mele&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
