SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en fait, dans la realite&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;avec le temps ces alveoles se &lt;/span&gt;&lt;span &gt;collabent&lt;/span&gt;&lt;span &gt;: atelectasie. et dans u poumon normal ces zones n'existent pratiquement pas. en revanche ce sont des phenomenes que l'on peut rencontrer en cas de pathologie. cela produit un phenomene dont&lt;/span&gt;&lt;span &gt; on a parle avant: c'est le shunt. a la difference du shunt anatomique, ce shunt la se produit au niveau des alveoles pulmonaires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
