SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span  fontstyle='italic'&gt;dans ce chapitre nous allons voir comment l'oxygene&lt;/span&gt;&lt;span  fontstyle='italic'&gt; et le co2 sont transportes entre les &lt;/span&gt;&lt;span  fontstyle='italic'&gt;alveoles &lt;/span&gt;&lt;span  fontstyle='italic'&gt;et les &lt;/span&gt;&lt;span  fontstyle='italic'&gt;mitochondries tissulaires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
