// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@transport de l'oxygene@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide2.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'o2 est transporte sous forme dissoute dans le plasma, et a la fois sous forme dissoute et liee a l'&lt;/span&gt;&lt;span &gt;hb&lt;/span&gt;&lt;span &gt; dans les hematies. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@loi de henry@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 2;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide3.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;avant de poursuivre, il est important de rappeler la loi de henry qui concerne la dilution des gaz dans un milieu liquide. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lorsque l'on met en presence un gaz comme l'o2 //, et un liquide , comme l'eau dans un recipient clos //, le gaz diffuse non seulement dans la phase gazeuse, mais aussi  dans la phase liquide //. c'est un processus qui demande un certain temps mais qui aboutit a un etat d'equilibre entre la px dans la phase gazeuse et la concentration dans la phase liquide. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@loi de henry@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la concentration du gaz dans la phase liquide est proportionnelle a la px dans la phase gazeuse, ici j'augmente la px de l'o2 et au bout d'un temps, la concentration de l'o2 dissoute dans l'eau sera aura atteint un niveau plus eleve. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@loi de henry@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la loi de henry dit que la [c] du gaz dans la phase liquide depend de la px multipliee par un coefficient. ce coefficient est le coefficient de solubilite. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une consequence directe de la loi de henry est qu'on peut, et c'est habituellement le cas, exprimee la concentration d'un gaz dissoute en terme de pression partielle. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par exemple, si on dit que la pression partielle de l'o2 dans le sang arteriel est de 100 &lt;/span&gt;&lt;span &gt;mmhg&lt;/span&gt;&lt;span &gt; ou 13.3 kpa,  cela signifie que si on met le sang en contact avec une phase gazeuse, a l'equilibre la px de l'o2 sera de 13.3 kpa dans cette phase gazeuse. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@solubilite des gaz@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide6.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le coefficient de solubilite alpha depend de la nature du gaz, des proprietes &lt;/span&gt;&lt;span &gt;phsyico-chimiques&lt;/span&gt;&lt;span &gt; du solvant. il est exprime en ml gaz/ ml solvant / &lt;/span&gt;&lt;span &gt;mmhg&lt;/span&gt;&lt;span &gt; (ou kpa). dans ce tableau vous avez des valeurs de &lt;/span&gt;&lt;span &gt;coeff&lt;/span&gt;&lt;span &gt; de &lt;/span&gt;&lt;span &gt;sollubilite&lt;/span&gt;&lt;span &gt; pour les gaz qui nous interessent &lt;/span&gt;&lt;span &gt;cad&lt;/span&gt;&lt;span &gt; l'o2 et le co2 et l'azote. vous pouvez voir que le co2 est beaucoup plus soluble que l'o2 par exemple, lui-meme plus soluble que l'azote. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@transport de l'oxygene@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 3;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide7.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;100ml de sang&lt;/span&gt;&lt;span &gt; on peut dissoudre au maximum // 0.003 ml d'o2&lt;/span&gt;&lt;span &gt; par &lt;/span&gt;&lt;span &gt;mmhg&lt;/span&gt;&lt;span &gt; de pao2, a 37° c. &lt;/span&gt;&lt;span &gt;cad&lt;/span&gt;&lt;span &gt; que si on laisse&lt;/span&gt;&lt;span &gt; 100 ml de sang arteriel dans un recipient ferme, au contact d'une phase gazeuse avec une po2 de 100 &lt;/span&gt;&lt;span &gt;mmhg&lt;/span&gt;&lt;span &gt; //, qui est la p partielle de l'o2 dans le sang arteriel, alors on aura au total 0.3 ml d'o2 // sous forme dissoute. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;si on suppose un debit cardiaque // a 5 l/min, cela veut dire qu'on peut transporter au maximum 15 ml d'o2 par minute. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;or, les besoins // sont = a 250 a 300 ml/min, et peuvent atteindre 4000 ml/min pour un effort vigoureux. donc, la quantite d'o2 transportee sous forme dissoute // et clairement insuffisante face aux besoins. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
