SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;c'est la raison&lt;/span&gt;&lt;span &gt; pour laquelle la part la plus importante du transport de l'o2 est assuree par l'&lt;/span&gt;&lt;span &gt;hb&lt;/span&gt;&lt;span &gt;. voyons d'abord la structure et les proprietes de l'&lt;/span&gt;&lt;span &gt;hb&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
