// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@i - anatomie@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 2;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide2.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;i - anatomie&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les glandes salivaires se divisent  en 2 groupes :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les glandes salivaires principales, comprenant les glandes parotides, sub mandibulaires et sub linguales, toutes paires et symetriques&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les glandes salivaires accessoires disseminees sur toute l'etendue de la muqueuse buccale (levres, palais, langue)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@a/ glande parotide@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 3;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide3.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;a/ glandes  parotides (plus grosse des glandes salivaires)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	situees en avant de l'oreille (para = a cote ; otis = oreille), elles occupent les loges parotidiennes. elles sont de forme prismatique, de couleur jaunatre et possedent 2 lobes (un profond et un superficiel). elles pesent environ 25 g.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@glande parotide@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 2;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;elles  secretent une salive sereuse qui s'ecoule au travers du conduit parotidien (anciennement : canal de stenon). ce conduit a un trajet quasi horizontal, schematise par une ligne (entre la partie inferieure du tragus et la levre superieure). &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@ostium  conduit parotidien@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il s'ouvre dans la cavite buccale en regard de la 2&lt;/span&gt;&lt;span &gt;eme&lt;/span&gt;&lt;span &gt; molaire superieure. il mesure environ 4 cm de long&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@glande parotide@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 11;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide6.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;elles entretiennent un rapport etroit avec les nerfs faciaux, qui les traverse et se divisent en leur sein en deux branches :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- temporo faciale, qui donnera a son tour les rameaux temporaux, zygomatiques et une partie des rameaux buccaux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cervico faciale, qui se divisera ensuite en rameaux cervical, marginal de la mandibule et une partie des rameaux buccaux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette ramification se fait dans le meme plan, et divise schematiquement la parotide en un lobe superficiel et un profond.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le nerf facial est la 7eme paire cranienne. il assure la motricite des muscles de la face et la sensibilite d'une partie de l'oreille. il est donc a l'origine de la mimique faciale et sa lesion cause une paralysie faciale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@b/ glande sub mandibulaire@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 5;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide7.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;b/ glandes  sub mandibulaires&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	secondes par leur taille, elles se situent dans les regions sous mandibulaires, en arriere des branches horizontales de la mandibule. elles excretent la salive, plutot sero muqueuse, via les conduits sub mandibulaire ou canaux de wharton qui traverse le plancher buccal. ils presentent, lors de leurs trajets, un coude qui croise le nerf lingual.  nerf lingual qui assure la sensibilite de l'hemilangue du meme cote. les ostium se trouvent  de part et d'autre du frein de la langue. son trajet est plus long et mesure environ 7 cm.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@c/ glande sub linguale@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide8.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;c/ glandes  sub  linguales&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	elles se situent sous la muqueuse du  plancher buccal, en avant des glandes sous mandibulaires  et se drainent directement dans la bouche via le conduit sub lingual.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@d/ glandes accessoires@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide9.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les glandes salivaires accessoires se situent directement sous la muqueuse au niceau de toute la cavite buccale. elles assurent l'humidification de base de la bouche alors que les glandes salivaires principales assurent les decharges de salive, principalement pendant les repas.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@ii - embryologie@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 2;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide10.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ii - embryologie&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	les glandes salivaires proviennent de l'ectoderme et de l'endoderme de la region du pharynx et se forment aux alentours de la 6&lt;/span&gt;&lt;span &gt;eme&lt;/span&gt;&lt;span &gt; semaine de vie intra uterine.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	il existe dans le sillon entre les bourgeons maxillaires et mandibulaires, une invagination de l'ectoderme qui va s'enfoncer dans le mesenchyme (en gardant un orifice) pour donner le conduit parotidien et son ostium. l'autre cote, borgne se differencie pour devenir le parenchyme parotidien.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	au niveau du plancher oral, des invaginations endodermiques seront a l'origine des glandes sous mandibulaires et sous linguales.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@iii - histologie et physiologie@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide11.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;iii - histologie et physiologie&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	les glandes principales sont aussi appelees « extrinseques » et les accessoires « intrinseques » du fait de leur localisation intra murale, dans le chorion de la cavite buccale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@a/ structure@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 4;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide12.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;a -  structure &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	ce sont des glandes acineuses, entourees d'une capsule de conjonctive, elle-meme envoyant des elements formant des travees. cela separe les glandes en lobes et lobules.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	elles sont composees de 2 parties : les acini et les canaux excreteurs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@1/ acini@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 6;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide13.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;1/ les acini&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	il existe differentes « sortes » de salive excretees par les differentes glandes : la salive sereuse, sero-muqueuse ou muqueuse et donc des acini differents.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'acinus est un  amas de cellules excretrices regroupees autour d'un canal collecteur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;acini sereux : au niveau parotidien surtout. ils sont de petites tailles et spheriques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;acini muqueux : au niveau des glandes sub linguales principalement. ils sont plus allonges, « tubulo-acini » et forme une lumiere de plus grand diametre&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;acini mixte, sero-muqueux : au niveau des glandes sous mandibulaires. ils se presentent comme une couronne de cellules muco-secretantes autour de la lumiere associee a un croissant de cellules sereuses.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@2/ canaux excreteurs@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 4;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide14.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;2/ les  canaux excreteurs&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;voies excretrices intra lobulaires (excreteurs et secreto-excreteurs) ou la presence de cellules cylindriques qui sont a l'origine d'une activite metabolique intense avec presence de nombreuses enzymes au pole apical. cela participe au remaniement chimique de la salive initialement excretee.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@2/ canaux excreteurs@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 3;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide15.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voies excretrices inter lobulaires (excreteurs purs) formees de cellules epitheliales&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;autour des acini et des canaux, il existe des cellules myoepitheliales (donc contenant des myofibrilles contractiles) qui permettent l'excretion de la salive ainsi qu'une aide dans la regulation du debit.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@b/ physiologie@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide16.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;b - physiologie&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	annexees au tube digestif, elles participent a la digestion par des secretions exocrines et endocrines. mais elles ont aussi un role de protection.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
