SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;1/ &lt;/span&gt;&lt;span  fontweight='bold'&gt;contenu de la secretion : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	nous secretons entre 1000 et 1500 ml de salive par jour, la majeure partie etant reabsorbee par le tube digestif. le debit de base est de 0.5 ml/min.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la salive contient : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;eau&lt;/span&gt;&lt;span &gt; (99,5 %), &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;des &lt;/span&gt;&lt;span  fontweight='bold'&gt;electrolytes&lt;/span&gt;&lt;span &gt; dans des proportions dependant de la stimulation des glandes, &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	les ions sodium, potassium, calcium, hydrogene (h+), bicarbonateset des metaux (le cuivre et le fer). &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	la concentration en &lt;/span&gt;&lt;span  fontweight='bold'&gt;nacl &lt;/span&gt;&lt;span &gt;est faible (1/7 par rapport au plasma) et celle en &lt;/span&gt;&lt;span  fontweight='bold'&gt;glucose&lt;/span&gt;&lt;span &gt; est nulle 	pour ne pas perturber les differents « gouts » percus au niveau de notre langue.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	son &lt;/span&gt;&lt;span  fontweight='bold'&gt;ph&lt;/span&gt;&lt;span &gt; varie entre 6.7 et 8.5 ; lie aux ions h+ et tamponnes par les ions bicarbonates&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;du &lt;/span&gt;&lt;span  fontweight='bold'&gt;mucus&lt;/span&gt;&lt;span &gt; (qui va la rendre plus ou moins visqueuse et donc favoriser la stase et potentiellement la formation de calculs),qui est compose de mucines (glycopeptides : 75% proteines et 25 % hydrate de carbone)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
