SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;des enzymes : &lt;/span&gt;&lt;span  fontweight='bold'&gt;amylase, lipase  et lysozyme&lt;/span&gt;&lt;span &gt; essentiellement qui sont les enzymes utiles a la digestion&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;des proteines  extrinseques (&lt;/span&gt;&lt;span  fontweight='bold'&gt;issues du plasma&lt;/span&gt;&lt;span &gt;) (albumine, &lt;/span&gt;&lt;span  fontweight='bold'&gt;immunoglobuline&lt;/span&gt;&lt;span &gt; de type a,g,m) &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et des &lt;/span&gt;&lt;span  fontweight='bold'&gt;iga produites&lt;/span&gt;&lt;span &gt; directement par les glandes salivaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;quelques cellules epitheliales et lymphocytaires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il existe aussi une composante endocrine (nerve growth factor : &lt;/span&gt;&lt;span  fontweight='bold'&gt;ngf&lt;/span&gt;&lt;span &gt;, pour les glandes sous mandibulaires, epidermal growth factor : &lt;/span&gt;&lt;span  fontweight='bold'&gt;egf&lt;/span&gt;&lt;span &gt;, renine, angiotensine …) en faible proportion.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
