SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la salive dite primaire est essentiellement issu du plasma. elle est filtree au travers des acini et passe ensuite dans les canaux excreteurs. elle subit peu de modifications. elle est dite « isotonique » au plasma mais riche en k+.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
