SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;digestion :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la premiere partie de la digestion est assuree par les dents, veritables broyeurs, par le biais de la mastication, ou les aliments fragmentes vont etre dilues avec la salive. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les enzymes digestives (via l'amylase, la lipase et la maltase)  vont agir pour dissoudre les aliments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les glucides sont les premiers a etre attaques par l'amylase salivaire qui clive l'amidon en maltose (2 molecule de glucose)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les substances dissoutes se fixent alors sur les recepteurs gustatifs qui peuvent etre stimules. les aliments ainsi dilues et lubrifies par le mucus contenu dans la salive sont plus facilement avales (aide a la deglutition)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elle permet ainsi via ses mucines de maintenir une humidite buccale, ce qui aide, entre autre, a la phonation (il est plus facile de parler lorsque notre bouche est humide).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
