SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;4/ la &lt;/span&gt;&lt;span  fontweight='bold'&gt;regulation &lt;/span&gt;&lt;span &gt;de la salive se fait sous un double systeme : nerveux et hormonal. commencons par la partie nereuse&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;nerveux &lt;/span&gt;&lt;span &gt;: &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il existe des circuits reflexes court conditionne decrit par pavlov : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par stimulation visuelle (une jolie fille), olfactive (l'odeur du pain chaud), ou gustative&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et une regulation par le systeme nerveux autonome sans stimulus, via les systeme parasympathique (nerf glossopharyngien ix et facial vii) et sympathique (ganglion cervical superieur).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@reflexe salivaire simple et conditionne@";
SDpagedocid[SDpagenum] = SDdocnum;
