SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide25.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;	&lt;/span&gt;&lt;span  textdecoration='underline'&gt;reflexe du systeme nerveux autonome&lt;/span&gt;&lt;span &gt; : les systemes sympathique et para sympathique n'ont pas d'action antagoniste au niveau des glandes salivaires ; ils stimulent tout deux la secretion mais de facon differente.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les neuro transmetteurs des 2 systemes se fixent sur des recepteurs membranaires et stimule le mecanisme cellulaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;pΣ (dominant)&lt;/span&gt;&lt;span &gt; : donne une salive sereuse, riche en enzymes, abondante et fluide. c'est aussi la faible activite tonique de ses fibres (continuellement stimulees) qui permet l'humidification basale de la bouche (par exemple : les glandes accessoires ne sont soumises qu'aux fibres pΣ et secretent de la salive de facon continue).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le neuro transmetteur est l'acetylcholine. il se fixe sur des recepteurs muscariniques. cela conduit a un « vaso dilatation » des plexus vasculaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

