SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span  fontweight='bold'&gt;Σ &lt;/span&gt;&lt;span &gt;: donne une salive peu abondante, epaisse, riche en mucus. c'est la « bouche seche » lors du trac ou des situations de peur ou le systeme Σ est active.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le neuro transmetteur est la noradrenaline qui se fixe sur les recepteurs adrenergiques alpha et beta. cela permet la « vidange » des glandes par contraction des cellules myoepitheliales.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
