SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;concretement, une ou plusieurs &lt;/span&gt;&lt;span  fontweight='bold'&gt;voies d'administration &lt;/span&gt;&lt;span &gt;sont identifiees sur la base des donnees toxicologiques, pharmacologiques et pharmacocinetiques de la substance active.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le galeniste en se basant sur les connaissances physico-chimiques du principe actif va operer des choix qualitatifs et quantitatifs en excipients a associer a ce principe actif pour realiser la forme galenique la plus adequate : cette selection operee sur une demarche scientifique constitue &lt;/span&gt;&lt;span  fontweight='bold'&gt;les phases de pre-formulation et de formulation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans le meme temps, le galeniste doit rechercher la ou les methodes de fabrication les mieux adaptees pour transformer les matieres premieres selectionnees en medicament ou produit fini : &lt;/span&gt;&lt;span  fontweight='bold'&gt;c'est la phase de de developpement du procede de fabrication.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;bien que cela ne soit pas indique sur le triptyque, le &lt;/span&gt;&lt;span  fontweight='bold'&gt;galeniste reflechi en parallele aux articles de conditionnement, primaire et externe, &lt;/span&gt;&lt;span &gt;qui accueilleront la forme galenique fabriquee.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
