SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'ensemble des missions du galeniste requiere un certain nombre de competences :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le galeniste doit etre un specialiste des sciences de la matiere, a ce point de vue, il doit&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;connaitre l'origine, la nature des composants du medicament qu'il elabore,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il doit maitriser les techniques &lt;/span&gt;&lt;span &gt;pysico-chimiques&lt;/span&gt;&lt;span &gt; pour caracteriser les composants du medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le galeniste doit etre attentif aux sciences de l'ingenieur, en effet il doit developper les operations&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pharmaceutiques, encore appelees operations unitaires, &lt;/span&gt;&lt;span &gt;auterment&lt;/span&gt;&lt;span &gt; dit, les techniques et appareillages&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;permettant de transformer les matieres premieres en produit d'usage fini.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, ma collegue mme &lt;/span&gt;&lt;span &gt;deletraz&lt;/span&gt;&lt;span &gt;, vous dira au prochain cycle que le medicament est un produit d'usage tres encadre… le galeniste doit connaitre parfaitement le cadre &lt;/span&gt;&lt;span &gt;reglementaire&lt;/span&gt;&lt;span &gt; dans lequel il evolue, afin de proposer&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un medicament respectant soit les directives, soit les recommandations edictees par les autorites competentes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
