SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;avant d'aborder l'etude detaillee des composants des medicaments, il convient de completer la classification des medicaments, brievement&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;abordees au cours du cycle 1. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;j'ai choisi deux classifications, l'une basee sur la reglementation qui permettra de definir&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;successivement les specialites pharmaceutques, les preparations magistrales et les&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;medicaments officinaux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la deuxieme classification est basee quant a elle sur les methodes therapeutiques&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et permettra de positionner l'allopathie a l'homeopathie&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
