SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les &lt;/span&gt;&lt;span &gt;specialites pharmaceutiques (art. l 511-1, csp) se definissent comme&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;« tout medicament prepares a l 'avance, presente sous un conditionnement particulier, et caracterise par une &lt;/span&gt;&lt;span &gt;denomi-nation&lt;/span&gt;&lt;span &gt; speciale »&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold' fontstyle='italic'&gt;ex.&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;efferalgan&lt;/span&gt;&lt;span  fontweight='bold'&gt;®&lt;/span&gt;&lt;span  fontweight='bold'&gt; 500 effervescent = comprime de paracetamol, boite de 16 comprimes, conditionnes sous film &lt;/span&gt;&lt;span  fontweight='bold'&gt;thermosoude&lt;/span&gt;&lt;span  fontweight='bold'&gt; pvc/alu (chlorure de polyvinyle / aluminium).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- &lt;/span&gt;&lt;span &gt;representent plus de 98 % des medicaments en vente sur le marche… &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- sont fabriquees en quantite industrielle selon les bpf. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- commercialisation soumise a une autorisation prealable (amm) delivree par une autorite competente.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
