SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;bonjour a tous, je suis le professeur denis wouessidjewe.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans le cadre de cette ue intitulee initiation a la connaissance du medicament, nous allons passer quelques heures ensemble pour effectivement decouvrir, connaitre et comprendre les aspects technologiques et biopharmaceutique du medicament, a travers les voies d'administration des medicaments et les formes galeniques correspondantes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
