SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les objectifs pedagogiques seront consideres comme atteints, si les notions suivantes sont acquises :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	identifier les differentes composantes du medicament&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	d'ou proviennent ces composants et comment les selectionne-t-on ?&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	connaitre les origines de ces composants et le rationnel de leurs choix ?&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	connaitre le role de ces composants et les methodologies de leurs association pour en faire des medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	les voies d'administration ou d'introduction des medicaments dans le corps humain etant nombreuses et specifiques, comment adapte-on la forme galenique pour satisfaire aux exigences de chaque voie d'administration ?&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, tout au long du cours, nous nous familiariserons avec une vocabulaire pharmaceutique et plus particulierement un vocabulaire correspondant a la pharmacie galenique encore appelee pharmacotechnie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
