SDpagedisplay[SDpagenum] = "Slide1.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la definition technologique du medicament decoulant en partie de la definition legale de  medicament, il est opportun de vous en rappeler&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les mots cles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette definition legale stipule que le medicament est une substance ou une composition… lire la dia… &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette definition deja evoquee par le pr ribuot dans son cours d'introduction sera completer et commentee en detail par mme deletraz dans le cycle 3 relatif aux aspects reglementaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour ma part et pour la definition technologique des medicaments, je retiendrais essentiellement les deux notions qui definissent le medicament comme etant une substance ou une composition.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide2.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;meme si la definition legale stipule qu'un medicament peut etre consideree comme une substance, en realite, ce n'est jamais le cas. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le medicament est plutot un produit multi composants, avec un nombre de composants qui peut etre eleve dans certains cas, par exemple jusqu'a 11 composants pour le comprime osmotique presente sue cette diapositive. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cependant, ces deux exemples ne doivent pas laisser penser que tous les medicaments contiennent toujours un chiffre eleve de composantes. en effet, on peut trouver des medicaments contenant seulement 3 a 4 composants.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide3.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;toutes ces observations nous amene donc a considerer que la definition technologique du medicament s'articule sur deux principales notions :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;d'une part, les composants encore appeles matieres premieres qui entrent dans la composition du medicament. d'autre part, ce medicament est presente sous une aspect physique adequat. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en ce qui concerne les composantes ou matieres premieres, on distingue deux principaux types de composants ou matieres premieres medicamenteuses.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; d'une part le ou les principes actifs, responsables des effets pharmacologiques. je vous renvoie aux differents cours precedents de mes collegues biologistes en particulier les cours de mlle stanke et monsieur ribuot pour les effets pharmacologiques des principes actifs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le deuxieme type de composant est constitue par les excipients. sous ce vocable excipient qui vient du latin « excipere qui signifie recevoir, on peut donc dire que les excipients sont des substances inertes non pharmacologiquement actives  charges de recevoir les principes actifs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en realite, ces excipients ne font pas que recevoir entre guillemet les principes actifs. nous verrons en effet tout au long de ce cours que leurs role peuvent etre importants et determinant pour l'activite du medicament.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'association du ou des principes actifs avec les excipients aboutit a un medicament presente sous une forme physique : on parle de forme galenique ou forme pharmaceutique ou parfois presentation galenique : vous devez etre familier pour les avoir deja utilises dans votre vie, des comprimes, des gelules, des pommades et autres sirops…&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
