SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le medicament prepare, que ce soient les comprimes, la pommade ou le sirop est protege par un ‘emballage' qu'on appelle de maniere plus elegante le conditionnement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on va distingue le conditionnement primaire, c-a-d l'emballage qui est en contact direct avec la forme galenique : par exemple un flacon en verre contenant le sirop, un tube ou flacon en aluminium contenant la pommade ou les comprimes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le flacon en verre ou les tubes en aluminium sont ensuite introduit dans un deuxieme emballage qui porte le nom de conditionnement externe et qui constitue une protection supplementaire du medicament.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous verrez avec mme deletraz que le conditionnement ne sert pas uniquement a proteger le medicament, mais il peut parfois aider a son administration, par exemple une seringue preremplie pour preparation a injecter. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;tout aussi importantes sont les informations legales…. voir suite&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
