SDpagedisplay[SDpagenum] = "Slide6.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;afin de se faire une idee concrete sur cet ensemble de notions concernant la definition technologique des medicaments, j'ai choisi de vous les illustrer avec deux exemples de medicaments :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il s'agit dans les deux exemples de formes galeniques de type comprime, administres par la voie orale. ce choix est dicte par le fait que les comprimes et la voie orale sont tres frequents dans l'utilisation des medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;autres raisons ayant dicte ce choix concernent la plus ou moins grande complexite dans la composition et la technologie des deux exemples de comprimes choisis.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'exemple 1 est un comprime effervescent…;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'exemple 2 est un comprime osmotique push pull…&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
