SDpagedisplay[SDpagenum] = "Slide7.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le comprime se presente sous la forme d'un compact de dimension appreciable autour de 2 cm de diametre. le conseil d'utilisation est d'introduire le comprime dans un demi-verre d'eau, d'attendre la fin de l'effervescence avant de boire la solution obtenue.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;que y a-t-il dans ce comprime et comment ca fonctionne ?&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
