SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;bonjour,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je m'appelle francoise stanke-labesque&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je suis maitre de conferences des universites a l'ufr de medecine et praticien hospitalier au chu de grenoble.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je suis pharmacologue : a ce titre, j'ai le plaisir de vous proposer une initiation a la pharmacologie, science du medicament.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour bien comprendre ce cours, j'attire votre attention sur la necessite d'avoir vu et acquis les notions concernant les principes actifs et leurs cibles presentes par le pr christophe ribuot dans cette ue6.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans ce premier chapitre, je vais vous presenter les aspects pharmacodynamiques de l'etude d'un medicament.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@@";
SDpagedocid[SDpagenum] = SDdocnum;
