SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme je vous l'ai indique en introduction, ce chapitre est une initiation a la pharmacodynamie dont voici une definition.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les objectifs principaux des etudes de pharmacodynamie sont, &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-d'une part, d'evaluer la relation concentration -effet ou la relation pharmacocinetique-pharmacodynamique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-d'autre part, de determiner l'intervalle therapeutique, cad l'intervalle des concentrations pour lequel le medicament est juge efficace et sur d'utilisation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces deux objectifs, relation concentration-effet et « index therapeutique » sont essentiels pour la determination du schema posologique optimal du medicament, ajuste ou non aux caracteristiques des malades.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
