SDpagenum++;
SDpagetitle[SDpagenum] = "@les niveaux d'etude de l'affinite et de l'activite des principes actifs@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les etudes de pharmacodynamie peuvent se faire a differents niveaux, dans le cadre d'etude in vitro, ou dans le cadre d'etude in vivo.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans le cadre d'etude in vitro, les etudes de pharmacodynamie peuvent se faire sur des preparations de recepteurs purifies, sur des fragments membranaires, sur des cellules isolees ou des organes isoles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les recepteurs purifies ou les fragments membranaires sont particulierement utilises pour realiser des etudes de liaison entre un ligand et son recepteur. ces etudes de liaison specifiques ne seront pas abordees dans ce chapitre;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en revanche, les cellules isoles et les organes isoles sont des preparations couramment utilisees pour la realisation d'etudes fonctionnelles  et donc pour la determination de courbes doses-reponse.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, les etude in vivo, realisee sur l'organisme entier sont egalement d'une grande utilite pour la realisation d'etudes fonctionnelles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

