SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dipositive 5 &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous allons dans un premier temps aborder la notion d'affinite d'un ligand pour son recepteur&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la puissance de l'interaction physico chimique entre un principe actif et son recepteur correspond a l'affinite reciproque de ces 2 partenaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette affinite depend de la structure chimique de chacun des partenaires permettant l'etablissement de liaisons non covalente entre le ligand et son recepteur;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
