// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@affinite et modele de la loi d'action de masse@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide6.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'interpretation des courbes doses-reponses fait appel a la theorie de l'occupation des recepteurs, &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en pharmacologie, la loi d'action de masse est largement appliquee pour quantifier l'interaction d'un ligand avec son recepteur&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette theorie pose comme postulat que la liaison des ligands (agonistes ou antagoniste) a leur recepteur obeit a la loi d'action de masse&lt;/span&gt;&lt;span &gt;, &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cela signifie qu'il s''etablit un equilibre dynamique entre les formes libres et associees du ligand et du recepteur&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette theorie de l'occupation des recepteur suppose que la reponse soit directement &lt;/span&gt;&lt;span &gt;proportionelle&lt;/span&gt;&lt;span &gt; au pourcentage de recepteurs occupes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;soit m, la concentration en medicament libre, &lt;/span&gt;&lt;span &gt;cad&lt;/span&gt;&lt;span &gt; non lie a son recepteur,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;r, la concentration en recepteur libre, &lt;/span&gt;&lt;span &gt;cad&lt;/span&gt;&lt;span &gt; non lie au medicament&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;mr, la concentration en complexe medicament -recepteur, &lt;/span&gt;&lt;span &gt;cad&lt;/span&gt;&lt;span &gt; medicament lie au recepteur,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il s'installera un equilibre entre la concentration en m et r et la concentration en medicament lie au recepteur, en fonction des constantes de cinetiques d'association k1 et de dissociation k2&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cet equilibre entre la concentration de medicament lie au recepteur et la concentration de medicament libre permet de definir la notion d'affinite d'un ligand pour son recepteur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

