SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;cette affinite est determinee par les etudes de liaisons, realisees tres frequemment sur des preparations membranaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;si l'on represente graphiquement la relation entre la concentration en ligand (en abscisse) et le % de recepteurs occupes par ce ligand (en ordonnee), on se rend compte que le pourcentage de recepteurs occupes par le ligand augmente avec la concentration en ligand, jusqu'a l'obtention d'un plateau correspondant a la totalite des recepteurs occupes par le ligand&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
