SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;si l'on transforme maintenant les concentrations reportees en abcisse par des valeurs&lt;/span&gt;&lt;span  fontweight='bold' fontstyle='italic'&gt; logarithmiques&lt;/span&gt;&lt;span &gt;, on constate que la courbe prend une forme en s &lt;/span&gt;&lt;span  fontweight='bold' fontstyle='italic'&gt;sigmoide&lt;/span&gt;&lt;span &gt;, avec une partie centrale pratiquement rectiligne qui sera d'une grande utilite pour retenir des parametres caracteristiques a partir de donnees experimentales. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette courbe presente l'avantage d''etre droite dans sa partie mediane, indiquant ainsi que l'effet est proportionnel au logarithme de la concentration&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
