SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous avons vu que la capacite d'un ligand a se fixer sur un recepteur caracterisait son affinite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous allons voir maintenant que la capacite du complexe (liand-recepteur) a induire un effet pharmacologique caracterise &lt;/span&gt;&lt;span  fontweight='bold'&gt;son activite intrinseque&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
