SDpagedisplay[SDpagenum] = "Slide19.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;cette theorie de l'occupation des recepteurs a ensuite evolue par l'observation que plusieurs ligands agissant sur le meme recepteur pouvaient induire des effets d'intensite variable;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comme vous pouvez le voir sur cette diapositive, et si je prends l'exemple de 2 agonistes 1 et 2 se fixant sur le meme recepteur,  l'amplitude de l'effet maximal n'est pas identique pour toutes les molecules se fixant sur ce recepteur : cette amplitude definit la notion d'activite intrinseque. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
