SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;a partir de l'effet maximum de la molecule la plus active, on peut definir &lt;/span&gt;&lt;span  fontweight='bold'&gt;3 types d'agonistes&lt;/span&gt;&lt;span &gt; selon leur valeur de emax&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span  fontweight='bold'&gt;agoniste entier&lt;/span&gt;&lt;span &gt; : emax = 100 % du emax de la molecule la plus  active . cet agonsite a une acitivite intrinseque egale a 1 et est considere comme un agoniste entier.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- &lt;/span&gt;&lt;span  fontweight='bold'&gt;agoniste partiel&lt;/span&gt;&lt;span &gt; : emax entre 0 et 100 % de l'emx et dont l'activite intrinseque est &amp;lt;1 mais est differente de zero&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous avez vu dans un cours precedent la notion d'antagoniste.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces antagonistes se lient comme les agonistes de facon specifique aux recepteurs mais n'entrainent pas d'effet: ce qui se traduit, sur cette representation graphique, par une courbe plate.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un pa presentant une activite intrinseque nulle est donc un antagoniste.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;en resume, et a retenir, l'activite intrinseque permet de  definir les agonistes entiers, les agonistes partiels, et les antagonistes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

