SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en resume, c'est ce que je vous demanderai de retenir, &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-l'ec50 ou concentration efficace 50 permet de determiner la puissance d'un agoniste.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la puissance d'un medicament depend de son affinite pour son site de liaison .&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-la valeur de emax ou effet maximum permet de caracteriser l'efficacite d'un medicament cad de mesurer l'efficacite avec laquelle l'interaction medicament-recepteur est couplee a l'effet.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'activite clinique d'un medicament depend de son activite maximale et de sa capacite a atteindre les recepteurs correspondants.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
