SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la determination de l'ec50 et de l'emax permet donc de comparer la puissance et l'efficacite de plusieurs agonistes se fixant sur le meme site de liaison.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a titre d'exemple, si l'on considere les 4 molecules a, b,c et d, qui sont des ligands d'un meme site de liaison.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour comparer la puissance de ces 4 molecules, il suffit de comparer l'ec50, et on peut en deduire que, en temre de puissance, a&amp;gt;b&amp;gt;c&amp;gt;d pour comparer l'efficacite de ces 4 molecules, il suffit de comparer leur emax : on peut donc en deduire que l'efficacite de la molecule d&amp;gt;a&amp;gt;b&amp;gt;c&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@comparaison de puissance et  d'efficacite de plusieurs ligands agonistes@";
