SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide26.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'etude systematique des relations concentrations-effet pour des analogues chimiques agonistes permet classiquement de definir les relations structure activites a l'interieur d'une meme serie chimique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la comparaison des ec50 et des emax permet de screener et de comparer les ligands d'un meme recepteur lors des phases pre-clinique de developpement d'un medicament.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@notion d'antagoniste@";
SDpagedocid[SDpagenum] = SDdocnum;
