SDpagedisplay[SDpagenum] = "Slide27.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous allons maintenant voir comment il est possible de quantifier la puissance d'un antagoniste.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je rappelle qu'il il existe des substances a effet antagoniste pur cad qui se fixent sur les recepteurs sans les stimuler. nous avons vu qu'en general, les antagonistes avaient une activite intrinseque nulle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
